mapper.select:
select id, title, source, pubdate, summary, type, views, flag, img
from
news



        and title like #{base}


        and user_id = #{condition.userId}


order by id ${condition.orderBy}
limit #{beginIndex}, #{pageSize}

select id, title, source, pubdate, summary, type, views, flag, img
from
news

    and flag = 0


        and title like #{base}


        and user_id = #{condition.userId}


order by id ${condition.orderBy}
limit #{beginIndex}, #{pageSize}

select count(id)
from
news



        and title like #{base}


        and user_id = #{userId}

select count(id)
from
news

    and flag = 0


        and title like #{base}


        and user_id = #{userId}

mapper.update:
update news set title = #{title}, source = #{source}, summary = #{summary}, type = #{type}
    where
    id = #{id}

update news
    set flag = 1
where id = #{id}

mapper.select:
